import React, { useState } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import { documentService } from '../../services/api';

const DocumentValidator = () => {
  const [formData, setFormData] = useState({
    cpf: '',
    rg: ''
  });
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const formatCPF = (value) => {
    // Remove caracteres não numéricos
    const numericValue = value.replace(/\D/g, '');

    // Limita a 11 dígitos
    const truncated = numericValue.slice(0, 11);

    // Aplica a máscara XXX.XXX.XXX-XX
    if (truncated.length <= 3) {
      return truncated;
    } else if (truncated.length <= 6) {
      return `${truncated.slice(0, 3)}.${truncated.slice(3)}`;
    } else if (truncated.length <= 9) {
      return `${truncated.slice(0, 3)}.${truncated.slice(3, 6)}.${truncated.slice(6)}`;
    } else {
      return `${truncated.slice(0, 3)}.${truncated.slice(3, 6)}.${truncated.slice(6, 9)}-${truncated.slice(9)}`;
    }
  };

  const formatRG = (value) => {
    // Remove caracteres não numéricos
    const numericValue = value.replace(/\D/g, '');

    // Limita a 9 dígitos (padrão comum para RG)
    const truncated = numericValue.slice(0, 9);

    // Aplica a máscara XX.XXX.XXX-X
    if (truncated.length <= 2) {
      return truncated;
    } else if (truncated.length <= 5) {
      return `${truncated.slice(0, 2)}.${truncated.slice(2)}`;
    } else if (truncated.length <= 8) {
      return `${truncated.slice(0, 2)}.${truncated.slice(2, 5)}.${truncated.slice(5)}`;
    } else {
      return `${truncated.slice(0, 2)}.${truncated.slice(2, 5)}.${truncated.slice(5, 8)}-${truncated.slice(8)}`;
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let formattedValue = value;

    if (name === 'cpf') {
      formattedValue = formatCPF(value);
    } else if (name === 'rg') {
      formattedValue = formatRG(value);
    }

    setFormData(prev => ({
      ...prev,
      [name]: formattedValue
    }));
  };

  const validateDocument = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      // Remove formatação antes de enviar para o backend
      const cleanedData = {
        cpf: formData.cpf.replace(/\D/g, ''),
        rg: formData.rg.replace(/\D/g, '')
      };

      const response = await documentService.validateDocument(cleanedData);
      setResult(response.data);
    } catch (err) {
      setError('Erro ao validar documentos. Tente novamente mais tarde.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Validação de Documentos</h2>
      <Card className="mt-3">
        <Card.Body>
          <Form onSubmit={validateDocument}>
            <Form.Group className="mb-3">
              <Form.Label>CPF</Form.Label>
              <Form.Control
                type="text"
                name="cpf"
                placeholder="000.000.000-00"
                value={formData.cpf}
                onChange={handleChange}
                maxLength={14}
                required
              />
              <Form.Text className="text-muted">
                Formato: 000.000.000-00
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>RG</Form.Label>
              <Form.Control
                type="text"
                name="rg"
                placeholder="00.000.000-0"
                value={formData.rg}
                onChange={handleChange}
                maxLength={12}
                required
              />
              <Form.Text className="text-muted">
                Formato: 00.000.000-0
              </Form.Text>
            </Form.Group>

            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Validando...' : 'Validar Documentos'}
            </Button>
          </Form>
        </Card.Body>
      </Card>

      {result && (
        <Alert 
          variant={result.status === 'VALIDO' ? 'success' : 'danger'} 
          className="mt-3"
        >
          <Alert.Heading>
            {result.status === 'VALIDO' ? 'Documentos Válidos' : 'Documentos Inválidos'}
          </Alert.Heading>
          <p>{result.mensagem}</p>
          {result.detalheValidacao && (
            <div className="mt-2">
              <strong>Detalhes da Validação:</strong>
              <p>{result.detalheValidacao}</p>
            </div>
          )}
        </Alert>
      )}

      {error && (
        <Alert variant="danger" className="mt-3">
          <Alert.Heading>Erro</Alert.Heading>
          <p>{error}</p>
        </Alert>
      )}
    </div>
  );
};

export default DocumentValidator;
