import React, { useState } from 'react';
import { Form, Button, Card, Alert, Container, Row, Col } from 'react-bootstrap';
import { useNavigate, Link } from 'react-router-dom';
import authService from '../../services/auth';
import './Register.css';

const Register = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Validar senhas
    if (userData.password !== userData.confirmPassword) {
      setError('As senhas não coincidem');
      setLoading(false);
      return;
    }

    try {
      const { name, email, password } = userData;
      const result = await authService.register(name, email, password);

      if (result.success) {
        // Após registro bem-sucedido, fazer login automático
        const loginResult = await authService.login(email, password);
        if (loginResult.success) {
          navigate('/');
        } else {
          // Registro bem-sucedido, mas falha no login automático
          navigate('/login');
        }
      } else {
        setError(result.error);
      }
    } catch (err) {
      setError('Erro ao registrar. Por favor, tente novamente.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col md={6} lg={5}>
          <Card className="register-card">
            <Card.Body className="p-4">
              <div className="text-center mb-4">
                <h2 className="fw-bold">Criar Conta</h2>
                <p className="text-muted">Registre-se para acessar o sistema</p>
              </div>

              {error && (
                <Alert variant="danger" className="mb-4">
                  {error}
                </Alert>
              )}

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Nome</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={userData.name}
                    onChange={handleChange}
                    placeholder="Seu nome completo"
                    required
                    className="custom-input"
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={userData.email}
                    onChange={handleChange}
                    placeholder="seu@email.com"
                    required
                    className="custom-input"
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Senha</Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={userData.password}
                    onChange={handleChange}
                    placeholder="********"
                    required
                    className="custom-input"
                    minLength="6"
                  />
                </Form.Group>

                <Form.Group className="mb-4">
                  <Form.Label>Confirmar Senha</Form.Label>
                  <Form.Control
                    type="password"
                    name="confirmPassword"
                    value={userData.confirmPassword}
                    onChange={handleChange}
                    placeholder="********"
                    required
                    className="custom-input"
                    minLength="6"
                  />
                </Form.Group>

                <Button 
                  variant="primary" 
                  type="submit" 
                  disabled={loading}
                  className="w-100 btn-register"
                >
                  {loading ? 'Registrando...' : 'Registrar'}
                </Button>
              </Form>

              <div className="text-center mt-4">
                <p>
                  Já tem uma conta?{' '}
                  <Link to="/login" className="login-link">
                    Faça login
                  </Link>
                </p>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
