import React, { useState } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import { documentService } from '../../services/api';

const DocumentValidator = () => {
  const [formData, setFormData] = useState({
    cpf: '',
    rg: ''
  });
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateDocument = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await documentService.validateDocument(formData);
      setResult(response.data);
    } catch (err) {
      setError('Erro ao validar documentos. Tente novamente mais tarde.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Validação de Documentos</h2>
      <Card className="mt-3">
        <Card.Body>
          <Form onSubmit={validateDocument}>
            <Form.Group className="mb-3">
              <Form.Label>CPF</Form.Label>
              <Form.Control
                type="text"
                name="cpf"
                placeholder="Digite o CPF (apenas números)"
                value={formData.cpf}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>RG</Form.Label>
              <Form.Control
                type="text"
                name="rg"
                placeholder="Digite o RG (apenas números)"
                value={formData.rg}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Validando...' : 'Validar Documentos'}
            </Button>
          </Form>
        </Card.Body>
      </Card>

      {result && (
        <Alert 
          variant={result.status === 'VALIDO' ? 'success' : 'danger'} 
          className="mt-3"
        >
          <Alert.Heading>
            {result.status === 'VALIDO' ? 'Documentos Válidos' : 'Documentos Inválidos'}
          </Alert.Heading>
          <p>{result.mensagem}</p>
          {result.detalheValidacao && (
            <div className="mt-2">
              <strong>Detalhes da Validação:</strong>
              <p>{result.detalheValidacao}</p>
            </div>
          )}
        </Alert>
      )}

      {error && (
        <Alert variant="danger" className="mt-3">
          <Alert.Heading>Erro</Alert.Heading>
          <p>{error}</p>
        </Alert>
      )}
    </div>
  );
};

export default DocumentValidator;
